INSERT INTO lc_notas VALUES("49","732","NA","pt","<p>Entre 1975 e 1979 dirigiu a Escola de Engenharia da UFRJ. No governo de Chagas Freitas, 1979, foi nomeado chefe da Secretaria de Indr&uacute;stria e Com&eacute;rcio (SEIC). E em junho de 1980 nomeado prefeito do Estado do Rio de Janeiro.</p>","2013-10-08 17:01:28","25");
