INSERT INTO lc_notas VALUES("65","750","NA","pt","<p class=\"western\" align=\"JUSTIFY\"><span style=\"font-family: Times New Roman,serif;\"><span style=\"font-size: medium;\">Comandante do DOI e Tenente-Coronel. Foi ouvido como testemunha no IPM, declarando sobre a natureza da miss&atilde;o do DOI recebida pelos elementos vitimados pela explos&atilde;o.</span></span></p>","2013-09-26 15:23:51","26");
INSERT INTO lc_notas VALUES("66","751","NA","pt","<p class=\"western\" align=\"JUSTIFY\"><span style=\"font-family: Times New Roman,serif;\"><span style=\"font-size: medium;\">Coronel Chefe da 2&ordf; Se&ccedil;&atilde;o do Estado-Maior do I Ex&eacute;rcito. Transmitiu ao Comandante do DOI/I Ex&eacute;rcito ordens para realizar miss&atilde;o de observar o show promovido pelo Cebrade no Riocentro na noite de 30 de abril de 1981.</span></span></p>","2013-09-26 15:26:12","26");
