INSERT INTO lc_notas VALUES("119","792","NA","pt","<p>Afastamento: 02.04 a 11.04.1962, por motivo de viagem, per&iacute;odo em que o Presidente da<br />C&acirc;mara dos Deputados exerceu a Presid&ecirc;ncia da Rep&uacute;blica.&nbsp;</p>","2013-10-28 16:59:48","2");
